---------------- NODES TABLE CREATION AND INSERTIONS ---------------
DROP TABLE IF EXISTS NODES;
CREATE TABLE NODES (ID INT PRIMARY KEY, ID2 INT, NAME VARCHAR(1024), TEXT VARCHAR(2048), TIME VARCHAR(127));
---------------- LINKS TABLE CREATION AND INSERTIONS ---------------
DROP TABLE IF EXISTS LINKS;
CREATE TABLE IF NOT EXISTS LINKS (ID INT PRIMARY KEY, SOURCE INT, DESTINATION INT, VALUE DOUBLE);
