INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (2,2,'Reale Objekte','Die Ursprünge der Mathematik liegen im Zählen von Objekten und der Untersuchung von Eigenschaften realer Objekte. Die Pyramiden in Ägypten, die ca 4.500 v.Chr. Erbaut wurden, zeigen bereits das Vorhandensein mathematischer Kenntnisse.','/');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (3,3,'Ebene Geometrie - Fläche von Dreieck, Viereck, Kreis (Pi = 256/81)','Der Moskauer Papyrus ist ein altägyptischer Papyrus mit mathematischem Inhalt, der eine Sammlung von insgesamt 25 Rechenaufgaben enthält. Er ist 5,44 m lang, nur 8 cm breit und wird etwa auf das Jahr 1850 v. Chr. Datiert. Er enthält unter anderem Aufgaben zur Berechnung der Fläche des Dreiecks, des Vierecks und des Kreises, wobei hier die Näherung Pi = 256/81 verwendet wird.','1850 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (4,4,'Raumgeometrie - Volumen verschiedener Körper','Der Moskauer Papyrus ist ein altägyptischer Papyrus mit mathematischem Inhalt, der eine Sammlung von insgesamt 25 Rechenaufgaben enthält. Er ist 5,44 m lang, nur 8 cm breit und wird etwa auf das Jahr 1850 v. Chr. Datiert. In Aufgabe 14 wird exakt das Volumen eines quadratischen Pyramidenstumpfes berechnet.','1850 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (5,5,'Raumgeometrie - Oberfläche verschiedener Körper','Der Moskauer Papyrus ist ein altägyptischer Papyrus mit mathematischem Inhalt, der eine Sammlung von insgesamt 25 Rechenaufgaben enthält. Er ist 5,44 m lang, nur 8 cm breit und wird etwa auf das Jahr 1850 v. Chr. Datiert. In Aufgabe 10 wird beispielsweise die Oberfläche der Halbkugel korrekt berechnet (mit Pi = 256/81).','1850 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (6,6,'Allgemeine Regeln zur Flächenberechnung','Das Wissen über die babylonische Mathematik stützt sich auf ca 400 Tontafeln, die seit 1850 ausgegraben wurden. Einige dieser Tafeln geben Formeln für die Flächenberechnung verschiedener ebener Figuren an.','1700 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (7,7,'Satz des Pythagoras','Das Wissen über die babylonische Mathematik stützt sich auf ca 400 Tontafeln, die seit 1850 ausgegraben wurden. Einige dieser Tafeln geben die Formel des Satz des Pythagoras an.','1700 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (8,8,'Kreisumfang und Kreisfläche (Pi = 3)','Das Wissen über die babylonische Mathematik stützt sich auf ca 400 Tontafeln, die seit 1850 ausgegraben wurden. Tafeln, die Formeln zur Kreisfläche und zum Kreisumfang enthalten, benutzen die Näherung Pi=3.','1700 BS');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (9,9,'Allgemeine Regeln zur Volumenberechnung','Das Wissen über die babylonische Mathematik stützt sich auf ca 400 Tontafeln, die seit 1850 ausgegraben wurden. Einige dieser Tafeln geben Formeln für die Volumenberechnung verschiedener Figuren an.','1700 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (10,10,'Flächeninhalt gleichschenkliges Dreieck (A=1/2 * g * h)','Der Papyrus Rhind ist eine altägyptische Abhandlung zu verschiedenen mathematischen Themen, die man heute als Arithmetik, Algebra, Geometrie, Trigonometrie und Bruchrechnung bezeichnen würde. Er gilt neben dem etwas älteren, aber weniger umfangreichen Moskauer Papyrus als eine der wichtigsten Quellen für das heutige Wissen über die Mathematik im Alten Ägypten und wird auf etwa 1550 v. Chr. datiert. Er enthält eine Formel zur Berechnung des Flächeninhaltes eines gleichschenkligen Dreiecks, nämlich A = 1/2 * g * h. ','1550 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (11,11,'Kreisumfang und Kreisfläche (Pi = 256/81)','Der Papyrus Rhind ist eine altägyptische Abhandlung zu verschiedenen mathematischen Themen, die man heute als Arithmetik, Algebra, Geometrie, Trigonometrie und Bruchrechnung bezeichnen würde. Er gilt neben dem etwas älteren, aber weniger umfangreichen Moskauer Papyrus als eine der wichtigsten Quellen für das heutige Wissen über die Mathematik im Alten Ägypten und wird auf etwa 1550 v. Chr. datiert. Bei Aufgabe 48 zur Berechnung der Fläche des Kreises leitet Ahmes über ein Quadrat, dem ein Kreis einbeschrieben ist, die Näherung Pi = 256/81 her.','1550 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (12,12,'Volumen Zylinder (Pi = 256/81)','Der Papyrus Rhind ist eine altägyptische Abhandlung zu verschiedenen mathematischen Themen, die man heute als Arithmetik, Algebra, Geometrie, Trigonometrie und Bruchrechnung bezeichnen würde. Er gilt neben dem etwas älteren, aber weniger umfangreichen Moskauer Papyrus als eine der wichtigsten Quellen für das heutige Wissen über die Mathematik im Alten Ägypten und wird auf etwa 1550 v. Chr. datiert. Bei Aufagbe 41 geht es um die Berechnung des Volumens eines zylindrischen Kornspeichers. Dabei wird die Näherung Pi = 256/81 verwendet.','1550 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (13,13,'Trigonometrie: Steigung Pyramide = Gegenkathete/Ankathete','Der Papyrus Rhind ist eine altägyptische Abhandlung zu verschiedenen mathematischen Themen, die man heute als Arithmetik, Algebra, Geometrie, Trigonometrie und Bruchrechnung bezeichnen würde. Er gilt neben dem etwas älteren, aber weniger umfangreichen Moskauer Papyrus als eine der wichtigsten Quellen für das heutige Wissen über die Mathematik im Alten Ägypten und wird auf etwa 1550 v. Chr. datiert. Die Steigung einer Pyramide wird angegeben als das Verhältnis von Gegenkathete zu Ankathete.','1550 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (14,14,'Satz des Thales','Thales von Milet wurde wahrscheinlich um 624 v. Chr geboren und starb zwischen 548 und 544 v. Chr. Er war ein vorsokratischer Naturphilosoph, Geometer und Astronom des archaischen Griechenlands. Der Satz, der nach Thales benannt wurde, kann wie folgt formuliert werden: Konstruiert man ein Dreieck aus den beiden Endpunkten des Durchmessers eines Halbkreises und einem weiteren Punkt dieses Halbkreises, so erhält man immer ein rechtwinkliges Dreieck. Dieser Halbkreis wird auch als Thaleskreis bezeichnet.','580 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (15,15,'Strahlensatz','Thales von Milet wurde wahrscheinlich um 624 v. Chr geboren und starb zwischen 548 und 544 v. Chr. Er war ein vorsokratischer Naturphilosoph, Geometer und Astronom des archaischen Griechenlands. Überlieferungen zufolge benutzte er als erster den Strahlensatz, um die Höhe einer Pyramide zu bestimmen, indem er die Länge des Schattens der Pyramide zu dem Zeitpunkt maß, zu dem sein eigener Schatten genauso groß wie er selbst war. Der Wahrheitsgehalt dieser Überlieferung wird heute angezweifelt. Der Strahlensatz gehört zu den wichtigsten Aussagen der Elementargeometrie. Er befasst sich mit Streckenverhältnissen und ermöglicht es bei vielen geometrischen Überlegungen, unbekannte Streckenlängen auszurechnen. ','580 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (16,16,'Beweis des Satz des Pythagoras','Pythagoras von Samos wurde 570 v. Chr auf Samos geboren und starb nach 510 v. Chr. in Metapont. Er war ein antiker griechischer Philosoph und Gründer einer einflussreichen religiös-philosophischen Bewegung, den Pythagoreern. Von Pythagoras ist der erste Beweis des nach ihm benannten Satzes überliefert, obwohl die Aussage des Satzes des Pythagoras schon deutlich länger bekannt war.','520 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (17,17,'Dreieck Kongruenzsätze','Pythagoras von Samos wurde 570 v. Chr auf Samos geboren und starb nach 510 v. Chr. in Metapont. Er war ein antiker griechischer Philosoph und Gründer einer einflussreichen religiös-philosophischen Bewegung, den Pythagoreern. Die Kongruenzsätze für Dreiecke waren schon Pythagoras bekannt. Noch heute sind die Kongruenzsätze SSS, SWS, WSW, SsW ein wichtiger Inhalt des Lehrplans an allen Schularten.','520 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (18,18,'Dreieck Innenwinkelsumme','Pythagoras von Samos wurde 570 v. Chr auf Samos geboren und starb nach 510 v. Chr. in Metapont. Er war ein antiker griechischer Philosoph und Gründer einer einflussreichen religiös-philosophischen Bewegung, den Pythagoreern. Von Pythagoras ist der erste Beweis überliefert, dass die Innenwinkelsumme im Dreieck gleich 2R ist.','520 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (19,19,'Dreieck Inn- und Umkreis','Pythagoras von Samos wurde 570 v. Chr auf Samos geboren und starb nach 510 v. Chr. in Metapont. Er war ein antiker griechischer Philosoph und Gründer einer einflussreichen religiös-philosophischen Bewegung, den Pythagoreern. Die Konstruktion des Inn- und Umkreises eines Dreiecks stammt von Pythagoras.','520 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (20,20,'allgemeine Proportionenlehre (irrationale Zahlen, Verhältnisse, Verhältnisgleichungen)','Eudoxos von Knidos wurde wohl zwischen 397 und 390 v. Chr. in Knidos geboren und starb zwischen 345 und 338 v. Chr. ebendort. Er war ein griechischer Mathematiker, Astronom, Geograph, Arzt, Philosoph und Gesetzgeber der Antike. Eudoxos begründete die allgemeine Proportionenlehre. Dabei konnte er erstmals die irrationalen Größen einbeziehen, da seine Proportionenlehre auch auf inkommensurable Größen anwendbar ist. Seine Definitionen von Verhältnis (lógos) und Proportion (analogía) sind im fünften Buch von Euklids Elementen überliefert. ','360 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (21,21,'Volumen Pyramide = 1/3 Volumen Prisma','Eudoxos von Knidos wurde wohl zwischen 397 und 390 v. Chr. in Knidos geboren und starb zwischen 345 und 338 v. Chr. ebendort. Er war ein griechischer Mathematiker, Astronom, Geograph, Arzt, Philosoph und Gesetzgeber der Antike. Er untersuchte die Volumenverhältnisse von Körpern und zeigte, dass das Volumen einer Pyramide einem Drittel des entsprechenden Prismas entspricht.','360 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (22,22,'Kegelvolumen = 1/3 Zylindervolumen','Eudoxos von Knidos wurde wohl zwischen 397 und 390 v. Chr. in Knidos geboren und starb zwischen 345 und 338 v. Chr. ebendort. Er war ein griechischer Mathematiker, Astronom, Geograph, Arzt, Philosoph und Gesetzgeber der Antike. Er untersuchte die Volumenverhältnisse von Körpern und zeigte, dass das Volumen eines Kegels einem Drittel des entsprechenden Zylinders entspricht.','360 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (23,23,'Exhaustionsmethode','Eudoxos von Knidos wurde wohl zwischen 397 und 390 v. Chr. in Knidos geboren und starb zwischen 345 und 338 v. Chr. ebendort. Er war ein griechischer Mathematiker, Astronom, Geograph, Arzt, Philosoph und Gesetzgeber der Antike. Er untersuchte die Volumenverhältnisse von Körpern. Dazu verwendete er ein infinitesimales Berechnungsverfahren, die Exhaustionsmethode. Bei der Exhaustionsmethode geht es darum, eine gegebene Figur von Innen möglichst komplett auszuschöpfen, also z.B. die Kreisfläche durch regelmäßige n-Ecke anzunähern, wobei n immer größer wird, sodass der tatsächliche Unterschied zwischen Kreisfläche und Fläche des n-Ecks beliebig klein wird.','360 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (24,24,'Axiomatisierung der Geometrie','Euklid von Alexandria war ein griechischer Mathematiker, der wahrscheinlich im 3. Jahrhundert v. Chr. in Alexandria gelebt hat. In seinem berühmtesten Werk Elemente trug er das Wissen der griechischen Mathematik seiner Zeit zusammen. Er zeigte darin die Konstruktion geometrischer Objekte, natürlicher Zahlen sowie bestimmter Größen und untersuchte deren Eigenschaften. Dazu benutzte er Definitionen, Postulate und Axiome. Viele Sätze der Elemente stammen offenbar nicht von Euklid selbst. Seine Hauptleistung besteht vielmehr in der Sammlung und einheitlichen Darstellung des mathematischen Wissens sowie der strengen Beweisführung, die zum Vorbild für die spätere Mathematik wurde. Das bekannte fünfte Postulat der ebenen euklidischen Geometrie, das Parallelenaxiom, besagt: Schneiden zwei Geraden eine Strecke so, dass die auf einer Seite von der Strecke und den zwei Geraden eingeschlossenen zwei Winkel kleiner als 180° sind, dann schneiden sich die beiden Geraden auf dieser Seite und begrenzen zusammen mit der Strecke ein Dreieck. ','320 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (25,25,'Höhensatz, Kathetensatz, Kosinussatz','Euklid von Alexandria war ein griechischer Mathematiker, der wahrscheinlich im 3. Jahrhundert v. Chr. in Alexandria gelebt hat. Der Höhensatz und der Kathetensatz werden zur Satzgruppe des Pythagoras für das rechtwinklige Dreieck gezählt. Der Kosinussatz ist die Verallgemeinerung des Satzes des Pythagoras auf nicht-rechtwinklige Dreiecke. Höhensatz, Kathetensatz und Kosinussatz werden in den Elementen von Euklid behandelt.','320 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (26,26,'beliebig genau Näherung für Pi (mit 96-Eck: 3 + 10/71 < Pi)','Archimedes von Syrakus wurde um 287 v. Chr. vermutlich in Syrakus geboren und starb 212 v. Chr. Ebenda. Er war ein griechischer Mathematiker, Physiker und Ingenieur. Archimedes bewies, dass sich der Umfang eines Kreises zu seinem Durchmesser genauso verhält wie die Fläche des Kreises zum Quadrat des Radius. Er nannte dieses Verhältnis noch nicht Pi, gab aber ein Verfahren mit einem n-Eck an, wie man sich dem Verhältnis bis zu einer beliebig hohen Genauigkeit nähern kann, vermutlich das älteste numerische Verfahren der Geschichte. Mit einem 96-Eck erhielt er die Näherung 3+10/71 (=3,140845...) < Pi.','225 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (27,27,'Flächen- und Volumenberechnungen (Form von Prinzip von Cavalieri)','Archimedes von Syrakus wurde um 287 v. Chr. vermutlich in Syrakus geboren und starb 212 v. Chr. Ebenda. Er war ein griechischer Mathematiker, Physiker und Ingenieur. Mit seinen Überlegungen zur Flächen- und Volumenberechnung nahm Archimedes Ideen der Integralrechnung viel später folgender Denker vorweg. Er ging dabei über die Eudoxos von Knidos zugeschriebene Exhaustionsmethode hinaus. Beispielsweise wandte er bereits eine Form des Prinzips von Cavalieri an. ','225 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (28,28,'Definition Ellipse, Kreis, Parabel durch Kegelschnitte','Appolonios von Perge wurde ca. 265 v. Chr. in Perge geboren und starb 190 v. Chr. in Alexandria. Er war ein antiker griechischer Mathematiker, bekannt für sein Buch über Kegelschnitte. In seinem bedeutendsten Werk Konika widmete er sich u.a. eingehend der Untersuchung von Kegelschnitten. Er wies nach, dass die vier verschiedenen Kegelschnitte (Ellipse, Kreis, Parabel und Hyperbel), deren Namen und Definitionen er einführte, vom selben allgemeinen Kegeltypus stammen.','210 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (29,29,'Apollonisches Problem','Appolonios von Perge wurde ca. 265 v. Chr. in Perge geboren und starb 190 v. Chr. in Alexandria. Er war ein antiker griechischer Mathematiker, bekannt für sein Buch über Kegelschnitte. Nach Appolonios von Perge ist auch das Appolonische Problem benannt. Dabei geht es um die Aufgab, mit Zirkel und Lineal die Kreise zu konstruieren, die drei beliebige vorgegebene Kreise berühren.','210 BC');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (30,30,'Heronverfahren','Heron von Alexandria war ein griechischer Mathematiker und Ingenieur, der wahrscheinlich im 1. Jahrhundert n. Chr. lebte. Seine genauen Lebensdaten sind nicht bekannt. Er lehrte am Museion von Alexandria, dessen Bibliothek berühmt war. Von Heron stammt das Heron-Verfahren zur approximativen Berechnung von Quadratwurzeln.','50');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (31,31,'Satz von Heron','Heron von Alexandria war ein griechischer Mathematiker und Ingenieur, der wahrscheinlich im 1. Jahrhundert n. Chr. lebte. Seine genauen Lebensdaten sind nicht bekannt. Er lehrte am Museion von Alexandria, dessen Bibliothek berühmt war. Nach Heron ist der Satz von Heron benannt, der es ermöglicht, die Fläche eines Dreiecks aus den drei Seitenlängen zu berechnen, ohne die Winkel zu kennen.','50');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (32,32,'Formel für Oberfläche von Pyramide, Zylinder, Kugel','Heron von Alexandria war ein griechischer Mathematiker und Ingenieur, der wahrscheinlich im 1. Jahrhundert n. Chr. lebte. Seine genauen Lebensdaten sind nicht bekannt. Er lehrte am Museion von Alexandria, dessen Bibliothek berühmt war. Sein Werk "Metrica" enthält Formeln zur Berechnung der Oberfläche von Pyramide, Zylinder und Kugel.','50');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (33,33,'Formel für Volumen von Pyramide, Zylinder, Kugel, Prisma','Heron von Alexandria war ein griechischer Mathematiker und Ingenieur, der wahrscheinlich im 1. Jahrhundert n. Chr. lebte. Seine genauen Lebensdaten sind nicht bekannt. Er lehrte am Museion von Alexandria, dessen Bibliothek berühmt war. Sein Werk "Metrica" enthält Formeln zur Berechnung des Volumens von Pyramide, Zylinder, Kugel und Prisma.','50');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (34,34,'Sphärische Geometrie','Menelaos wurde um 50 in Alexandria geboren und starb um 115 vermutlich in Rom. Er war ein antiker griechischer Mathematiker und Astronom. In seinem Werk Sphaerica beschäftigte er sich mit sphärischer Geometrie (Kugelgeometrie), unter anderem mit den für Astronomen wichtigen sphärischen Dreiecken.','90');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (35,35,'Sehnenfunktion (Vorläufer des Sinus)','Menelaos wurde um 50 in Alexandria geboren und starb um 115 vermutlich in Rom. Er war ein antiker griechischer Mathematiker und Astronom. In seinem Werk Sphaerica benutz Menelaos als erster ausdrücklich eben jene Sehnenfunktion, aus der später der Sinus entstand.','90');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (36,36,'Satz von Menelaos','Menelaos wurde um 50 in Alexandria geboren und starb um 115 vermutlich in Rom. Er war ein antiker griechischer Mathematiker und Astronom. Der nach ihm benannte Satz von Menelaos macht eine Aussage über die Teilungsverhältnisse, in denen eine Gerade ein Dreieck schneidet.','90');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (37,37,'Triangulation und Sehnentafeln (Vorläufer der Trigonometrie)','Claudius Ptolemäus wurde um 100 möglicherweise in Ptolemais Hermeiou, Ägypten geboren und starb nach 160 vermutlich in Alexandria. Er war ein griechischer Mathematiker, Geograf, Astronom, Astrologe, Musiktheoretiker und Philosoph. Er lebte in Alexandria in der römischen Provinz Ägypten. Insbesondere seine drei Werke zur Astronomie, Geografie und Astrologie galten in Europa bis zur frühen Neuzeit als wissenschaftliche Standardwerke und wichtige Datensammlungen. In seinem Werk Almagest, Buch 1, beschäftigt sich Claudius Ptolemäus mit der Sehnenberechnung und der Anwendung der Sehnentafeln bei astronomischen Dreiecksberechnungen.','140');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (38,38,'Satz des Ptolemäus über Sehnenvierecke','Claudius Ptolemäus wurde um 100 möglicherweise in Ptolemais Hermeiou, Ägypten geboren und starb nach 160 vermutlich in Alexandria. Er war ein griechischer Mathematiker, Geograf, Astronom, Astrologe, Musiktheoretiker und Philosoph. Er lebte in Alexandria in der römischen Provinz Ägypten. Insbesondere seine drei Werke zur Astronomie, Geografie und Astrologie galten in Europa bis zur frühen Neuzeit als wissenschaftliche Standardwerke und wichtige Datensammlungen. Der nach ihm benannte Satz von Ptolemäus ist ein Lehrsatz der Elementargeometrie, der eine Beziehung zwischen den Seiten und Diagonalen eines Sehnenvierecks beschreibt. Er lässt sich als Verallgemeinerung des Satzes von Pythagoras auf Sehnenvierecke auffassen.','140');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (39,39,'Formeln für Oberfläche und Volumen von Rotationskörper','Pappos war ein griechischer Mathematiker und Astronom. Er lebte im 4. Jahrhundert in Alexandria und war einer der letzten bedeutenden Mathematiker der Antike. Sein Hauptwerk, die Mathematischen Sammlungen, sind eine Hauptquelle für unsere Kenntnis der Geometrie in der Antike. Er bestimmte Oberfläche und Volumen von Rotationskörpern mit der Zentrobarischen Regel (heute Guldinsche Regeln).','350');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (40,40,'Satz von Pappos','Pappos war ein griechischer Mathematiker und Astronom. Er lebte im 4. Jahrhundert in Alexandria und war einer der letzten bedeutenden Mathematiker der Antike. Sein Hauptwerk, die Mathematischen Sammlungen, sind eine Hauptquelle für unsere Kenntnis der Geometrie in der Antike. Der nach ihm benannte Satz von Pappos ist ein zentraler Satz in der affinen und projektiven Geometrie.','350');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (41,41,'Flächenformel von Pappos (Pythagoras für beliebige Dreiecke mit Parallelogrammen statt Quadraten)','Pappos war ein griechischer Mathematiker und Astronom. Er lebte im 4. Jahrhundert in Alexandria und war einer der letzten bedeutenden Mathematiker der Antike. Sein Hauptwerk, die Mathematischen Sammlungen, sind eine Hauptquelle für unsere Kenntnis der Geometrie in der Antike. Die nach ihm benannte Flächenformel von Pappos behandelt eine wesentliche Verallgemeinerung des Satzes des Pythagoras und gilt für beliebige Dreiecke, wobei Parallelogramme anstelle der pythagoreischen Quadrate verwendet werden.','350');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (42,42,'Pi auf 3,1416 bestimmt','Aryabhata wurde um 476 in Ashmaka geboren und starb um 550 ebbendort. Er war ein bedeutender indischer Mathematiker und Astronom. Aryabhata bestimmte die Kreiszahl Pi für damalige Verhältnisse sehr genau auf 3,1416 und könnte schon geahnt haben, dass es sich um eine irrationale Zahl handelt.','520');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (43,43,'Grundlagen für Sinusfunktion ','Aryabhata wurde um 476 in Ashmaka geboren und starb um 550 ebbendort. Er war ein bedeutender indischer Mathematiker und Astronom. Aryabhata arbeitete an Sehnenfunktionen, die den Vorläufer des heutigen Sinus darstellen.','520');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (45,45,'Satz von Brahmagupta über Sehnenvierecke','Brahmagupta wurde 598 geboren und starb nach 665 und war ein indischer Mathematiker und Astronom. Er leitete das astronomische Observatorium in Ujjain und verfasste in dieser Funktion zwei Arbeiten zur Mathematik und zur Astronomie, das Brahmasphutasiddhanta im Jahre 628 und das Khandakhadyaka im Jahre 665. Der nach ihm benannte Satz von Brahmagupta  ist eine Aussage in der euklidischen Geometrie über Streckenverhältnisse in bestimmten Sehnenvierecken: Wenn die Diagonalen in einem Sehnenviereck aufeinander senkrecht stehen, dann teilt das Lot durch den Diagonalenschnittpunkt auf eine Seite deren gegenüberliegende Seite in zwei gleich große Strecken.','628');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (46,46,'Formel von Brahmagupta zur Berechnung der Fläche von  Sehnenvierecken','Brahmagupta wurde 598 geboren und starb nach 665 und war ein indischer Mathematiker und Astronom. Er leitete das astronomische Observatorium in Ujjain und verfasste in dieser Funktion zwei Arbeiten zur Mathematik und zur Astronomie, das Brahmasphutasiddhanta im Jahre 628 und das Khandakhadyaka im Jahre 665. Auf Brahmagupta geht auch die Formel zur Berechnung der Fläche von Sehnenvierecken zurück.','628');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (47,47,'Pi als Zahl, nicht wie bisher als Verhältnis','Die Banū Mūsā Brüder sind drei iranische Gelehrte, die im 9. Jahrhundert in Bagdad wirkten. Ihre Namen waren Jafar Muḥammad ibn Mūsā ibn Shākir, Aḥmad ibn Mūsā ibn Shākir und Al-Ḥasan ibn Mūsā ibn Shākir. Sie waren die ersten, die Pi (bzw. Kreisfläche und -umfang) als Zahl angaben und nicht wie die Griechen zuvor als Verhältnis von Umfang zu Durchmesser.','850');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (48,48,'Kugel- und Zylindervolumen als Zahl, nicht wie bisher als Verhältnis','Die Banū Mūsā Brüder sind drei iranische Gelehrte, die im 9. Jahrhundert in Bagdad wirkten. Ihre Namen waren Jafar Muḥammad ibn Mūsā ibn Shākir, Aḥmad ibn Mūsā ibn Shākir und Al-Ḥasan ibn Mūsā ibn Shākir. Sie waren die ersten, die das Kugel- und Zylindervolumen als Zahl angaben und nicht wie die Griechen zuvor als Verhältnisse.','850');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (49,49,'Einführung Sinus','Abu Abdallah Mohammad ibn Jabir Al-Battani wurde 850 in Harran, Mesopotamien (heute Türkei) geboren und starb 929 in Qasr al-Jiss, Irak. Er war Astronom und machte durch den Einsatz trigonometrischer Methoden präzisere Messungen als seine Vorgänger. So verwendete er erstmals die Sinusfunktion, und nicht die Sehnenfunktion wie bisher. Auch die Formel für rechtwinklige Dreiecke, b * sin(A) = a * sin(90°-A), stammt von ihm. ','900');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (50,50,'Identität tan = sin/cos ohne Bezeichnungen cos,tan','Abu Abdallah Mohammad ibn Jabir Al-Battani wurde 850 in Harran, Mesopotamien (heute Türkei) geboren und starb 929 in Qasr al-Jiss, Irak. Er war Astronom und machte durch den Einsatz trigonometrischer Methoden präzisere Messungen als seine Vorgänger. Er stellte als erster die Identität tan = sin/cos auf, jedoch ohne die Begriffe Kosinus und Tangens zu benutzen.','900');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (51,51,'Sinussatz + Beweis','Abu Abdallah Mohammad ibn Jabir Al-Battani wurde 850 in Harran, Mesopotamien (heute Türkei) geboren und starb 929 in Qasr al-Jiss, Irak. Er war Astronom und machte durch den Einsatz trigonometrischer Methoden präzisere Messungen als seine Vorgänger. Den Sinussatz formulierte al-Battani als erster in moderner Schreibweise und gab auch den ersten Beweis für diesen an.','900');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (52,52,'Erstmaliger Gebrauch des Tangens','Mohammad Abu''l-Wafa Al-Buzjani wurde am 10. Juni 940 in Buzjan geboren und starb am 15. Juli 998 in Bagdad. Er war ein herausragender persischer Mathematiker und Astronom des Mittelalters, welcher mehrere Bücher über angewandte Mathematik schrieb, verschiedene bedeutende trigonometrische Entdeckungen machte und mittlerweile verloren gegangene Kommentare zu den Werken von Euklid, Diophant und al-Chwarizmi verfasste. So gebrauchte er als erster den Begriff des Tangens.','980');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (53,53,'Einführung Sekans und Kosekans','Mohammad Abu''l-Wafa Al-Buzjani wurde am 10. Juni 940 in Buzjan geboren und starb am 15. Juli 998 in Bagdad. Er war ein herausragender persischer Mathematiker und Astronom des Mittelalters, welcher mehrere Bücher über angewandte Mathematik schrieb, verschiedene bedeutende trigonometrische Entdeckungen machte und mittlerweile verloren gegangene Kommentare zu den Werken von Euklid, Diophant und al-Chwarizmi verfasste. Er führte auch die trigonometrischen Funktionen Sekans und Kosekans ein.','980');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (54,54,'Vorschlag, trigonometrische Funktionen über Einheitskreis zu definieren','Mohammad Abu''l-Wafa Al-Buzjani wurde am 10. Juni 940 in Buzjan geboren und starb am 15. Juli 998 in Bagdad. Er war ein herausragender persischer Mathematiker und Astronom des Mittelalters, welcher mehrere Bücher über angewandte Mathematik schrieb, verschiedene bedeutende trigonometrische Entdeckungen machte und mittlerweile verloren gegangene Kommentare zu den Werken von Euklid, Diophant und al-Chwarizmi verfasste. Er war der erste, der vorschlug, die trigonometrischen Funktionen über den Einheitskreis zu definieren. ','980');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (55,55,'Sinussatz + Beweis für allgemeine sphärische Dreiecke','Mohammad Abu''l-Wafa Al-Buzjani wurde am 10. Juni 940 in Buzjan geboren und starb am 15. Juli 998 in Bagdad. Er war ein herausragender persischer Mathematiker und Astronom des Mittelalters, welcher mehrere Bücher über angewandte Mathematik schrieb, verschiedene bedeutende trigonometrische Entdeckungen machte und mittlerweile verloren gegangene Kommentare zu den Werken von Euklid, Diophant und al-Chwarizmi verfasste. Er bewies als erster den Sinussatz für allgemeine sphärische Dreiecke.','980');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (56,56,'Zusammenfassung und Übersetzung der Trigonometrie der Araber','Leonardo von Pisa wurde um 1170 in Pisa geboren und starb 1240 ebenda. Der Name Fibonacci ergab sich später aus "figlio di Bonaccio" („Sohn des Bonaccio“). Fibonacci war Rechenmeister in Pisa und gilt als einer der bedeutendsten Mathematiker des Mittelalters. Auf seinen Reisen nach Afrika, Byzanz und Syrien machte er sich mit der arabischen Mathematik vertraut und verfasste mit den dabei gewonnenen Erkenntnissen im Jahr 1202 das Rechenbuch Liber abbaci.','1202');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (57,57,'Formalisierung der ebenen und sphärischen Trigonometrie','Johann(es) Müller, später lateinisch genannt Regiomontanus „der Königsberger“, wurde am 6. Juni 1436 in Königsberg in Bayern geboren und starb am 6. Juli 1476 in Rom. Er war ein bedeutender Mathematiker, Astronom und Verleger des Spätmittelalters. 1462 bis 1464 verfasste er das Werk "De triangulis omnimodis", in dem er die bekannte ebene und sphärische Trigonometrie formalisierte und so den Grundstein für die neuzeitliche Trigonometrie legte.','1464');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (58,58,'Konstruktion von Figuren mit Zirkel und Lineal; Diskussion von Proportion und Perspektive','Albrecht Dürer wurde am 21. Mai 1471 in Nürnberg geboren und starb am 6. April 1528 ebenda. Er war ein deutscher Maler, Grafiker, Mathematiker und Kunsttheoretiker. In seinem Buch "Underweysung der messung mit dem zirckel und richtscheyt in Linien ebnen unnd gantzen corporen" beschäftigte er sich mit der Konstruktion spezielle Kurven (Muschellinie, Pascalsche Schnecke). Außerdem gibt er eine neue Konstruktion der Ellipse an und erkennt Ellipse, Parabel und Hyperbel als Kegelschnitte. Motiviert durch das ganz praktische Problem, die Schrifthöhe in Abhängigkeit von der Höhe ihrer Anbringung so zu staffeln, dass alle Zeilen gleich hoch erscheinen, beschäftigte er sich mit Proportion und Perspektive.','1525');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (59,59,'Definition trigonometrischer Funktionen über rechtwinkliges Dreieck','Georg Joachim Rheticus wurde am 16. Februar 1514 in Feldkirch geboren und starb am 4. Dezember 1574 in Kaschau. Er war ein deutscher Mathematiker, Astronom, Theologe, Kartograph, Instrumentenmacher und Mediziner. Er definierte als erster die trigonometrischen Funktionen über das rechtwinklige Dreieck.','1551');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (60,60,'Umfangreiche Tabellen für trigonometrische Funktionen','Georg Joachim Rheticus wurde am 16. Februar 1514 in Feldkirch geboren und starb am 4. Dezember 1574 in Kaschau. Er war ein deutscher Mathematiker, Astronom, Theologe, Kartograph, Instrumentenmacher und Mediziner. Er berechnete für alle sechs trigonometrischen Funktionen Tabellen mit einer Schrittweite von 10 Sekunden.','1551');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (61,61,'Geschlossene Formel für Kreiszahl Pi','François Viète wurde 1540 in Fontenay-le-Comte geboren und starb am 13. Dezember oder am 23. Februar 1603 in Paris. Er war ein französischer Advokat und Mathematiker. 1593 beschrieb er als erster eine geschlossene Formel für die Kreiszahl Pi in Form eines unendlichen Produkts.','1593');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (62,62,'Begründung der analytischen Geometrie','René Descartes wurde am 31. März 1596 in La Haye en Touraine geboren und starb am 11. Februar 1650 in Stockholm. Er war ein französischer Philosoph, Mathematiker und Naturwissenschaftler. In seinem Werk "La Géométrie"  verknüpfte er als erster Geometrie und Algebra und gilt somit als Begründer der analytischen Geometrie.','1637');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (63,63,'Satz von Desargues','Gérard Desargues wurde am 21. Februar 1591 in Lyon geboren und starb im Oktober 1661. Er war ein französischer Architekt und Mathematiker. Der nach ihm benannte Satz von Desargues spielt eine wichtige Rolle in den Grundlagen der Projektiven Geometrie.','1639');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (64,64,'Grundlagen der projektiven Geometrie','Gérard Desargues wurde am 21. Februar 1591 in Lyon geboren und starb im Oktober 1661. Er war ein französischer Architekt und Mathematiker. In seinem Werk "Brouillon Projet" entwicklete er die Grundlagen der Projektiven Geometrie, die aus Betrachtungen zur Perspektive entstand.','1639');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (65,65,'Satz von Pascal','Blaise Pascal wurde am 19. Juni 1623 in Clermont-Ferrand geboren und starb am 19. August 1662 in Paris. Er war ein französischer Mathematiker, Physiker, Literat und christlicher Philosoph. Der nach ihm benannte Satz von Pascal liefert eine Aussage zu Punkten auf einem Kegelschnitt in einer projektiven Ebene.','1640');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (66,66,'Prinzip von Cavalieri','Bonaventura Francesco Cavalieri wurde 1598 wahrscheinlich in Mailand geboren und starb am 30. November oder 3. Dezember 1647 in Bologna. Er war ein italienischer Jesuat, Mathematiker und Astronom. Das nach ihm benannte Prinzip von Cavalieri besagt, dass zwei Körper das gleiche Volumen haben, wenn all ihre Schnittflächen in Ebenen parallel zur Grundebene in entsprechenden Höhen den gleichen Flächeninhalt haben.','1647');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (67,67,'Konkrete algebraische Gleichungen und feste Symbolik für analytische Geometrie','Pierre de Fermat wurde 1607 in Beaumont-de-Lomagne gebroren und starb am 12. Januar 1665 in Castres. Er war ein französischer Mathematiker und Jurist. Er führte konkrete algebraische Gleichungen und eine feste Symbolik für die analytische Geometrie ein.','1650');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (68,68,'Satz von Ceva','Giovanni Ceva wurde am 1. September 1647 in Mailand geboren und starb am 13. Mai 1734 in Mantua. Er war ein italienischer Mathematiker. Der nach ihm benannte Satz von Ceva ist eine geometrische Aussage über Dreieckstransversalen. ','1678');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (69,69,'Exponential-, Sinus- und Kosinusreihe','Sir Isaac Newton wurde am 4. Januar 1643 in Woolsthorpe-by-Colsterworth geboren und starb am 31. März 1727 in Kensington. Er war ein englischer Naturforscher und Verwaltungsbeamter. Newton fand die Reihendarstellungen für die Exponential-, die Sinus- und die Kosinusreihe.','1704');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (70,70,'Begründer vieler Symbole: Pi für Kreiszahl, Standardbezeichnung Dreieck, i für sqrt(-1)','Leonhard Euler wurde am 15. April 1707 in Basel geboren und starb am 18. September 1783 in Sankt Petersburg. war ein Schweizer Mathematiker und Physiker und einer der bedeutendsten Mathematiker der Geschichte. Er war der Begründer vieler moderner Symbole, so verwendete er z.B. Pi für die Kreiszahl, die Standardbezeichnungen des Dreiecks und den Buchstaben i für die imaginäre Einheit.','1758');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (71,71,'Eulerscher Polyedersatz','Leonhard Euler wurde am 15. April 1707 in Basel geboren und starb am 18. September 1783 in Sankt Petersburg. war ein Schweizer Mathematiker und Physiker und einer der bedeutendsten Mathematiker der Geschichte. Der nach ihm benannte Eulersche Polyedersatz bringt die Anzahl der Ecken, Kanten und Flächen eines konvexen Polyeders in Zusammenhang.','1758');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (72,72,'Vereinfachung und Modernisierung der Elemente von Euklid','Adrien-Marie Legendre wurde am 18. September 1752 in Paris geboren und starb am 10. Januar 1833 ebenda. Er war ein französischer Mathematiker. In seinem 1794 erschienenen Geometrielehrbuch (Éléments de géométrie) vereinfachte und modernisierte er die Elemente des Euklids. Sein Lehrbuch war bis ins 19: Jahrhundert an Schulen weit verbreitet.','1794');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (73,73,'erste Ideen zur nichteuklidischen Geometrie','Johann Carl Friedrich Gauß wurde am 30. April 1777 in Braunschweig geboren und starb am 23. Februar 1855 in Göttingen. Er war ein deutscher Mathematiker, Astronom, Geodät und Physiker und wurde wegen seiner überragenden wissenschaftlichen Leistungen bereits zu seinen Lebzeiten als „Fürst der Mathematiker“ bezeichnet. Unabhängig von János Bolyai und Nikolai Lobatschewski beschäftigte sich Gauß mit dem Paralellenaxiom und bemerkte, dass dieses unabhängig von den anderen Axiomen der euklidischen Geometrie ist. Dies führte ihn zur nichteuklidischen Geometrie.','1811');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (74,74,'Gaußsche Zahlenebene','Johann Carl Friedrich Gauß wurde am 30. April 1777 in Braunschweig geboren und starb am 23. Februar 1855 in Göttingen. Er war ein deutscher Mathematiker, Astronom, Geodät und Physiker und wurde wegen seiner überragenden wissenschaftlichen Leistungen bereits zu seinen Lebzeiten als „Fürst der Mathematiker“ bezeichnet. Die nach ihm benannte Gaußsche Zahleneben liefert eine geometrische Veranschaulichung der komplexen Zahlen. ','1811');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (75,75,'Kontruktionskriterium für regelmäßiges n-Eck mit Zirkel und Lineal','Johann Carl Friedrich Gauß wurde am 30. April 1777 in Braunschweig geboren und starb am 23. Februar 1855 in Göttingen. Er war ein deutscher Mathematiker, Astronom, Geodät und Physiker und wurde wegen seiner überragenden wissenschaftlichen Leistungen bereits zu seinen Lebzeiten als „Fürst der Mathematiker“ bezeichnet. Gauß fand und bewies ein Konstruktionskriterium für regelmäßige n-Ecke mit Zirkel und Lineal und konstruierte ein regelmäßiges 17-Eck.','1811');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (76,76,'Feuerbachkreis','Karl Wilhelm Feuerbach wurde am 30. Mai 1800 in Jena geboren und starb am 12. März 1834 in Erlangen. Er war ein deutscher Mathematiker. Der nach ihm benannte Feuerbachkreis enthält 9 besondere Punkte des Dreiecks, nämlich die Mittelpunkte der Seiten, die Fußpunkte der Höhen und die Mittelpunkte der oberen Höhenabschnitte.','1822');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (77,77,'fundamentale Ideen der projektiven Geometrie: Invarianz des Doppelverhältnisses, projektive Verwandtschaften, Dualitätsprinzip, Poncelets Kontinuitätsprinzip','Jean-Victor Poncelet wurde am 1. Juli 1788 in Metz geboren und starb am 22. Dezember 1867 in Paris. Er war französischer Mathematiker, Ingenieur und Physiker. Poncelet gilt als einer der BEgründer der projektiven Geometrie, denn auf ihn gehen die Invarianz des Doppelverhältnisses, projektive Verwandtschaften, das Dualitätsprinzip und Poncelets Kontinuitätsprinzip zurück.','1823');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (78,78,'Nichteuklidische hyperbolische Geometrie','Nikolai Iwanowitsch Lobatschewski wurde am 1. Dezember 1792 in Nischni Nowgorod geboren und starb am 24. Februar 1856 in Kasan. Er war ein russischer Mathematiker. Lobatschewski fand eine Geometrie, in der das Parallelenaxiom nicht gilt, was zur nichteuklidischen hyperbolischen Geometrie führt.','1826');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (79,79,'homogene Koordinaten und Dualitätsprinzip für analytische Geometrie','August Ferdinand Möbius wurde am 17. November 1790 in Pforta geboren und starb am 26. September 1868 in Leipzig. Er war ein deutscher Mathematiker und Astronom an der Universität Leipzig. Möbius leistete mit der Einführung der homogenen Koordinaten und des Dualitätsprinzips wertvolle Beiträge zur analytischen Geometrie.','1827');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (80,80,'Arbeit an nichteuklidischen Geometrien','János Bolyai wurde am 15. Dezember 1802 in Klausenburg geboren und starb am 27. Januar 1860 in Neumarkt am Mieresch. Er war ein ungarischer Mathematiker. Bolyai fand, wie Gauß und Lobatschewski, eine nichteuklidische Geometrie, eine "absolut wahre (hyperbolische) Geometrie des Raumes". ','1831');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (81,81,'Begriff der absoluten Geometrie','János Bolyai wurde am 15. Dezember 1802 in Klausenburg geboren und starb am 27. Januar 1860 in Neumarkt am Mieresch. Er war ein ungarischer Mathematiker. Bolyai führte den Begriff der absoluten Geometrie ein, zu der er alle Aussagen zählte, bei denen er nicht spezifizierte, ob das Parallelenaxiom gelten soll oder nicht. ','1831');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (82,82,'Begründung der darstellenden Geometrie','Gaspard Monge wurde am 9. Mai 1746 in Beaune geboren und starb am 28. Juli 1818 in Paris. Er war ein französischer Mathematiker, Physiker und Chemiker sowie Minister. Er arbeitete an der Darstellung von dreidimensionalen Objekten im zweidimensionalen Raum und gilt damit als BEgründer der darstellenden Geometrie.','1847');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (83,83,'Riemann Integral','Georg Friedrich Bernhard Riemann wurde am 17. September 1826 in Breselenz geboren und starb am 20. Juli 1866 in Selasca. Er war ein deutscher Mathematiker und gilt als einer der bedeutendsten Mathematiker seiner Zeit. In seiner Habilitation entwicklete er das Riemann Integral, das beispielsweise im Schulunterricht verwendet wird, um die Fläche unter einer Kurve zu berechnen.','1857');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (84,84,'Klassifizierung verschiedener Geometrien','Felix Christian Klein wurde am 25. April 1849 in Düsseldorf geboren und starb am 22. Juni 1925 in Göttingen. Er war ein deutscher Mathematiker. In seinem sogenannten Erlanger Programm stellte er eine Systematisierung der damals bekannten verschiedenen Geometrien dar.','1871');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (85,85,'Poincarésches Kreisscheibenmodell und Poincarésches Halbebenenmodell für hyperbolische Geometrie','Jules Henri Poincaré wurde am 29. April 1854 in Nancy geboren und starb am 17. Juli 1912 in Paris. Er war ein bedeutender französischer Mathematiker, theoretischer Physiker, theoretischer Astronom und Philosoph. Auf ihn gehen zwei Modelle der hyperbolischen Geometrie zurück, nämlich das Poincarésche Kreisscheibenmodell und das Poincarésche Halbebenenmodell.','1890');
INSERT INTO NODES(ID,ID2,NAME,TEXT,TIME) VALUES (86,86,'Formaler axiomatischer Aufbau der Geometrie','David Hilbert wurde am 23. Januar 1862 in Königsberg geboren und starb am 14. Februar 1943 in Göttingen. Er war ein deutscher Mathematiker und gilt als einer der bedeutendsten Mathematiker der Neuzeit. In seinem fundamentalen Werk "Grundlagen der Geometrie" entwarf Hilbert für die euklidische Geometrie ein vollständiges Axiomensystem und baute darauf die komplette euklidische Geometrie streng axiomatisch auf. Hilbert wird der Ausspruch zugeschrieben, man könne statt „Punkte, Geraden und Ebenen“ jederzeit auch „Tische, Stühle und Bierseidel“ sagen. Es komme nur darauf an, dass die Axiome erfüllt sind. ','1899');
